mapper.insert:
insert into news (id, title, author, 
  publish_time, picture, is_hot, 
  is_publish, click_num, exam_status, 
  expansion1, expansion2, content
  )
values (#{id,jdbcType=INTEGER}, #{title,jdbcType=VARCHAR}, #{author,jdbcType=VARCHAR}, 
  #{publishTime,jdbcType=VARCHAR}, #{picture,jdbcType=VARCHAR}, #{isHot,jdbcType=INTEGER}, 
  #{isPublish,jdbcType=INTEGER}, #{clickNum,jdbcType=INTEGER}, #{examStatus,jdbcType=INTEGER}, 
  #{expansion1,jdbcType=VARCHAR}, #{expansion2,jdbcType=VARCHAR}, #{content,jdbcType=LONGVARCHAR}
  )
